export async function deleteBlogSectionById(blogId: string, sectionId: string) {
   try {
      const db = await getAppDB();
      const blogsCollection = db.collection("blogs");

      const result = await blogsCollection.updateOne(
         { _id: new ObjectId(blogId) },
         { $pull: { blogSections: { _id: sectionId } } }
      );

      if (!result.modifiedCount) {
         throw new Error(`Failed to delete the section with ID ${sectionId} from blog ${blogId}.`);
      }
      return { success: true, message: "Section deleted successfully." };
   } catch (error: any) {
      throw new Error(error?.message || "Failed to delete the blog section.");
   }
}

