import BlogProfile from "@/app/hire-talent/(marketing)/blogs/components/blog-profile";
import { SubscribeUser } from "../CTA/subscribe-user";
import BlogBanner from "./blog-banner";
import ExploreSection from "./explore-section";
import PopularReads from "./popular-reads";
import ProfileCard from "./profile-card";
import { DisplayUI } from "@/lib/enums";
import { getMarketingProfileByUserId } from "@/server-actions/services/profiles/profile-finder-services";
import { CategoryWiseBlogs } from "@/app/hire-talent/(marketing)/blogs/components/category-wise-blogs";
import ViewBlogProfiles from "./blog-profiles";

interface Props {
   blog: any;
}

export default async function BlogDetails({ blog }: Props) {
   const profile = await getMarketingProfileByUserId(blog?.author?.userId);
   return (
      <>
         <BlogBanner blog={blog} profile={profile} />
         <div className={"case-responsive-container md:py-3 md:mt-8 py-3 my-2"}>
            <div className={"grid"}>
               <div className="xl:col-9 md:col-8 col-12 sm:pr-6 relative">
                  <div className={"flex align-items-start gap-2"}>
                     <img src={"/gray-quotes.svg"} className="text-left h-full" width="30" height={100} alt="" />
                     <p className="text-16 mt-0">{blog?.blogDescription ? blog?.blogDescription : ""}</p>
                  </div>

                  <div>
                     {blog?.blogSections?.map((section: any, index: any) => (
                        <div className="grid" key={index}>
                           {section?.title && (
                              <div className="col-12 py-3">
                                 <div className="flex flex-column gap-2">
                                    <h2 className={"text-36 font-semibold text-secondary"}>{section?.title}</h2>
                                 </div>
                              </div>
                           )}
                           {section?.description && (
                              <div className="col-12 py-3">
                                 <div className="flex flex-column gap-2">
                                    <div dangerouslySetInnerHTML={{ __html: section?.description }} className="text-16"></div>

                                    {/* <div className="text-16">{section?.description}</div> */}
                                 </div>
                              </div>
                           )}
                           {section?.image && (
                              <div className="col-12 py-3 my-3">
                                 <div className="col-12 m-0 p-0 md:h-30rem sm:h-20rem h-15rem flex justify-content-center  border-1 surface-border bg-transparent relative group border-round">
                                    <div className="w-full">
                                       <img
                                          src={
                                             section?.image?.s3FileEntry?.url
                                                ? section?.image?.s3FileEntry?.url
                                                : "/noun-image-placeholder.svg"
                                          }
                                          className="h-full w-full border-round-lg bg-transparent"
                                          alt=""
                                          style={{ objectFit: "contain" }}
                                       />
                                    </div>
                                 </div>
                              </div>
                           )}

                           {section?.codeSnippet?.code && (
                              <div className={"col-12  my-3"}>
                                 <div className={"border-1 surface-border border-round bg-gray-100"}>
                                    <pre
                                       className={"overflow-y-scroll w-full px-3 language-javascript"}
                                       style={{
                                          whiteSpace: "pre-wrap",
                                          wordWrap: "break-word",
                                       }}
                                    >
                                       <code className={"language-javascript"}>{section?.codeSnippet?.code}</code>
                                    </pre>
                                 </div>
                              </div>
                           )}
                        </div>
                     ))}
                  </div>
                  <div className="pt-4">
                     <BlogProfile profile={profile} />
                  </div>
               </div>
               <div className="xl:col-3 md:col-4  sm:flex hidden">
                  <div
                     className="sticky overflow-auto hidden md:block"
                     style={{
                        msOverflowStyle: "none",
                        scrollbarWidth: "none",
                        top: "80px",
                        height: "200vh",
                     }}
                  >
                     <div className="flex gap-6 flex-column">
                        <PopularReads blog={blog} />
                        <ProfileCard profile={profile} />
                        <ExploreSection blog={blog} />
                        <SubscribeUser displayUI={DisplayUI.SubscribeCard} />
                     </div>
                  </div>
               </div>

               <div className="col-12 mt-4">
                  <CategoryWiseBlogs blogDetail={true} categoryCount={1} blogCount={3} category={blog?.categories[0]?.name} />
               </div>
            </div>
         </div>
         <div className="py-4">
            <SubscribeUser displayUI={DisplayUI.SubscriptionHero} />
         </div>
         <div className="mb-3">
            <ViewBlogProfiles />
         </div>
      </>
   );
}
